mapper.select:
select count(*)
from t_ordersetting
where orderDate = #{orderDate}

select *
from t_ordersetting
where orderDate between #{startDate} and #{endDate}

mapper.update:
update t_ordersetting
set number       = #{number},
    reservations = #{reservations}
where orderDate = #{orderDate}

update t_ordersetting
set reservations = #{reservations}
where orderDate = #{orderDate}
